var extMethod = {
    ToParams: function() {
        var json = arguments[0];
        return Object.keys(json)
            .map((key) => key + "=" + json[key])
            .join("&");
    },
    urlParse: function(u) {
        var dict = {};
        dict.Source = u;
        let params = (url) => {
            let queryParams = {};
            let reg = /([^?=&]+)=([^?=&]+)/g;
            url.replace(reg, function() {
                queryParams[arguments[1]] = arguments[2];
            });
            return queryParams;
        };
        var javaUrl = JavaImporter(java.net.URL);
        with(javaUrl) {
            var url = new URL(u);
            dict.Protocol = url.getProtocol();
            dict.Host = url.getHost();
            dict.Port = url.getPort();
            dict.File = url.getFile();
            dict.Path = url.getPath();
            dict.Ref = url.getRef();
            dict.Hosts = `${dict.Protocol}://${url.getHost()}`;
        }
        dict.Params = params(u);
        return dict;
    },
    isEqual: function(x, y) {
        const ok = Object.keys,
            tx = typeof x,
            ty = typeof y;
        return x && y && tx === "object" && tx === ty ?
            ok(x).length === ok(y).length &&
            ok(x).every((key) => this.isEqual(x[key], y[key])) : x === y;
    },
    findDifferentIndex: function(str1, str2) {
        const diffChars = [];
        for (let i = 0; i < str2.length; i++) {
            if (str1[i] !== str2[i]) {
                diffChars.push({
                    index: i,
                    s1: str1[i],
                    s2: str2[i],
                });
            }
        }
        return diffChars;
    },
    replaceCharAt: function(str, index, replacement) {
        if (index < 0 || index >= str.length) {
            return str;
        }
        //log(index)
        const arr = str.split("");
        arr[index] = replacement;
        return arr.join("");
    },
    getObjectKeysDiff: function(obj1, obj2) {
        const differentKeys = [];
        for (let key in obj1) {
            if (!obj2.hasOwnProperty(key) || obj1[key] !== obj2[key]) {
                differentKeys.push(key);
            }
        }
        for (let key in obj2) {
            if (!obj1.hasOwnProperty(key)) {
                differentKeys.push(key);
            }
        }
        return differentKeys;
    },
    getNextUrl: function(source, target, params) {
        var u1 = this.urlParse(source);
        var u2 = this.urlParse(target);
        var pa = undefined;
        if (typeof params == "object") {
            pa = params.hasOwnProperty("ç‰¹å¾") ? params.ç‰¹å¾ : undefined;
        }
        var nexturl = source;
        var ca = Object.keys(u2.Params).length == 0 ? "" : "?";
        if (pa != undefined) {
            var rs = String(u2.Path).replace(new RegExp(pa), "$1fypage");
            nexturl = u2.Hosts + rs + ca + this.ToParams(u2.Params);
        } else {
            if (this.isEqual(u1.Params, u2.Params)) {
                if (dt_debug) {
                    log(u1.Path);
                    log(u2.Path);
                }
                var df = this.findDifferentIndex(String(u1.Path), String(u2.Path));
                if (dt_debug) {
                    log(df);
                }
                if (df.length == 1) {
                    var re = df[0].index;
                } else {
                    var f = df.filter((x) => /\d+/.test(x.s2));
                    var re = f[0].index;
                }
                var eq = this.replaceCharAt(String(u2.Path), re, "fypage");
                nexturl = u2.Hosts + eq + ca + this.ToParams(u2.Params);
            } else {
                var ep = this.getObjectKeysDiff(u1.Params, u2.Params)
                if (ep.length == 1) {
                    u1.Params[ep[0]] = 'fypage'
                } else {
                    log(ep);
                }
                var eq = String(u1.Path).replace(/\d+/, "fypage");
                nexturl = u1.Hosts + eq + ca + this.ToParams(u1.Params);
            }
        }
        //log('next:'+nexturl)
        return nexturl;
    },
};

