    var info = { parse: parse, mark: shsort };
    if (stabs.some(x => x.hasOwnProperty('url'))) {
      info['urls'] = stabs.map(x => x.url);
    }

    for (var i = 0; i < tabs.length; i++) {
      var url = $('#noLoading#').lazyRule((vari, shsort, i, linec, info) => {

        var lists = storage0.getMyVar(shsort + '_list', '[]');

        if (getMyVar(vari + 'fold', '1') == '0') {
          var btn = findItem(shsort + '_button');
          var folds = ['<font color="#D3D3D3">☷', '<font color="#FFA500">☰'];
          var plist = storage0.getMyVar('er_playlists', '{}');

          var col = plist[shsort].col_type;
          var cls = plist[shsort].cls;
          var pic = plist[shsort].pic;
          var extra = plist[shsort].extra;
          //log(extra)
          updateItem(btn.extra.id, {
            title: btn.title.replace(folds[0], folds[1]),
          })

          putMyVar(vari + 'fold', '1');
          //return 'toast://展开才能操作';
        } else {
          var plist = storage0.getMyVar('er_playlists', '{}');

          var col = plist[shsort].col_type;
          var cls = plist[shsort].cls;
          var pic = plist[shsort].pic;
          var extra = plist[shsort].extra;
          //log(extra)
        }
        putMyVar(vari, i);

        var xlist = lists[i];
        if ($.func != undefined && $.func.constructor == Function) {
          deleteItemByCls(shsort);
          addItemAfter(shsort + '_line', {
            col_type: 'pic_1',
            img: 'https://img.nickyam.com/file/fe41a7a1744a3a8194df2.gif',
            url: 'toast://loading',
            extra: {
              cls: shsort
            }
          })
          xlist = $.func(i, info);
          storage0.putMyVar(shsort + '_list', xlist);
        }
        var plays = xlist.map(x => {
          return {
            title: x.title,
            url: x.url,
            col_type: col,
            pic: pic,
            extra: Object.assign({}, extra, {
              id: x.url.replace(/@lazyRule[\s\S]+/g, ''),
              cls: cls,
            })
          }
        })
        if (getMyVar(shsort + '_sort') == '1') {
          plays.reverse();
        }
        var pcfg = storage0.getMyVar(shsort + '_pushcfg', '{}');
        if (pcfg['单推']) {
          updateItem(shsort + '_push', {
            title: pcfg['obj']['单推'][0]
          })
          pcfg['单推'] = false;
        }
        if (pcfg['选推']) {
          updateItem(shsort + '_pushs', {
            title: pcfg['obj']['选推'][0]
          })
          pcfg['选推'] = false;
        }
        storage0.putMyVar(shsort + '_pushcfg', pcfg);
        deleteItemByCls(shsort);
        addItemAfter(shsort + '_line', plays);

        var tabx = findItemsByCls(shsort + '_tab');
        tabx.forEach((x, ii) => {
          var title = x.title;
          if (i == ii) {
            updateItem(x.extra.id, {
              title: title.replace(/#(\w+)?/g, linec),
            })
          } else {
            updateItem(x.extra.id, {
              title: title.replace(/#(\w+)?/g, '#'),
            })
          }
        })
        return 'toast://切换成功！';
