//随机颜色
var getRangeColors = function() {
    return '#' + ('00000' + (Math.random() * 0x1000000 << 0).toString(16)).substr(-6);
}

//动态分类
function dynamicClass(options) {
    const obj = {
        //默认值
        layout: "undefined",
        html: "empty",
        page: 0,
        分类颜色: "#FA7298",
        折叠: "““””<b>" + "∧".fontcolor("#1aad19") + "</b>",
        展开: "““””<b>" + "∨".fontcolor("#FF0000") + "</b>",
        open: true,
        state: MY_RULE.url,
        search: false,
        clears: [],
        clearall: ["yi_firstpage"],
        debug: false,

        预设(param) {
            if (param == undefined) {
                param = [];
            }
            Object.keys(param).forEach((property) => {
                // 对每个属性名执行操作
                const value = param[property]; // 获取属性值
                // 在这里执行您的操作
                this[property] = value;
                if (this.debug) {
                    console.log(property + ":" + value);
                }
            });
        },
        evalJSRule(item,
            jsRule) {
            let rule = jsRule.replace("@js:",
                "");
            rule = rule.trim();
            //log($.stringify(rule))
            let input = item;
            if (rule.startsWith("(")) {
                eval("result = " + rule);
            } else {
                /**
                 * 还原成 $.toString(...) 的最终结果，达到最终处理方式跟上面的 if 一致的目的
                 */
                eval("result = " + "(() => {" + rule + "})()");
            }
            return result || "";
        },
        getTitle(src, category) {
            let title = "";
            let titleRule = category || "a&&Text";
            if (titleRule.startsWith("@js:")) {
                title = this.evalJSRule(src, titleRule);
            } else {
                title = parseDomForHtml(src, titleRule);
            }
            return title || "";
        },
        getUrl(src, category) {
            let url = "";
            let urlRule = category.链接 || "a&&href";
            if (urlRule.startsWith("@js:")) {
                url = this.evalJSRule(src, urlRule);
            } else {
                url = pd(src, urlRule);
            }
            if (typeof category.链接处理 == "function") {
                url = category.链接处理(url);
            }
            return url || "";
        },
        isNull(param) {
            if (param == null) {
                return this;
            }
            return false;
        },
        isBoolean(variable) {
            if (typeof variable !== "boolean") {
                throw new Error("开启折叠请传入布尔值");
            }
        },
        清除(list) {
            if (this.isNull(list)) {
                return this;
            }
            if (list.constructor == String) {
                throw new Error("清除报错:请传列表");
            }
            this.clears = list;
            return this;
        },
        全清(list) {
            if (this.isNull(list)) {
                return this;
            }
            var c = this.clearall;
            list = c.concat(list);
            this.clearall = list;
            return this;
        },
        源码(input) {
            this.html = input;
            return this;
        },
        界面(input) {
            this.layout = input;
            return this;
        },
        页码(input) {
            this.page = input;
            return this;
        },
        状态(input) {
            this.state = input;
            return this;
        },
        开启搜索(input) {
            if (input == undefined) {
                input = true;
            }
            this.search = input;
            return this;
        },
        开启折叠(input) {
            if (input == undefined) {
                input = true;
            }
            this.isBoolean(input);
            this.open = input;
            return this;
        },
        setTitle(k, i, t) {
            return k == i ? "““””" + t.fontcolor(this.分类颜色) : t;
        },
        action(obj) {
            return [{
                title: "清除状态",
                js: $.toString((obj) => {
                    log(obj);
                    clearMyVar(obj.state);
                    clearMyVar("head.cate");
                    clearMyVar('cate_button');
                    obj.all.forEach((item) => {
                        clearMyVar(item);
                    });
                    obj.cl.forEach((item) => {
                        clearMyVar(item);
                    });
                    refreshPage();
                }, obj),
            }, ];
        },
        checkParam() {
            var obj = {};
            if (this.page == 0 && typeof page == "undefined") {
                throw new Error("请传入页码");
            } else {
                if (typeof page == "undefined") {
                    obj["页码"] = this.page;
                } else {
