      var obj = findItem(shsort + '_avatar')
      if (fold == '1') {
        var bcls = findItem(shsort + '_button');
        var tcls = findItemsByCls(shsort + '_tab');
        var lcls = findItem(shsort + '_line');

        var pcls = findItemsByCls(shsort);
        var cs = [bcls].concat(tcls, [lcls], pcls);

        if (push) {
          var pcls = findItemsByCls(shsort + '_pushtab');
          cs = pcls.concat(cs);
        }

        var cx = $.dataConvert(cs);
        storage0.putMyVar(shsort + '_cls', cx);
        if (push) {
          deleteItemByCls(shsort + '_pushtab');
        }
        deleteItem(bcls.extra.id);
        deleteItemByCls(shsort + '_tab');
        deleteItem(shsort + '_line');
        deleteItemByCls(shsort);

        updateItem(obj.extra.id, {
          desc: obj.desc.replace('展开<font color="#FFA500">☰', '折叠<font color="#D3D3D3">☷'),
        })
      }
      if (fold == '0') {
        var cs = storage0.getMyVar(shsort + '_cls');
        addItemAfter(shsort + '_avatar', cs);
        updateItem(obj.extra.id, {
          desc: obj.desc.replace('折叠<font color="#D3D3D3">☷', '展开<font color="#FFA500">☰'),
        })
      }
      putMyVar(foldname, fold === '1' ? '0' : '1');
      return 'toast://' + (fold === '1' ? '收起' : '展开');
    }, foldname, shsort, (parse['推送'] ? true : false));
    var desc = (fold === '0' ? 折叠 : 展开) + '\t';

    if (exp.length == 1) {
      url = 'hiker://empty';
      desc = '';
    }
    d.push({
      title: vari + ':\t\t' + '共' + String(tabs.length).fontcolor('#4682B4') + '条线路',
      desc: desc,
      col_type: 'avatar',
      img: custom.图标,
      url: url,
      extra: {
        id: shsort + '_avatar',
        longClick: this.setLongclick(shsort),
      }
    })
    if (typeof (parse['推送']) != 'undefined' && parse['推送'])
      this.pushTab('er_' + vari, shsort);

    if (fold == '0') {
      return
    }

    d.push({
      title: '““””' + title,
      url: $('#noLoading#').lazyRule((vari, shsort, custom) => {
        let conf = getMyVar(shsort + '_sort', '0');
        var clslists = findItemsByCls(shsort);
        var newclslist = $.dataConvert(clslists);
        deleteItemByCls(shsort);
        newclslist.reverse();
        addItemAfter(shsort + '_line', newclslist);

        var obj = findItem(shsort + '_button')
        var titles = [`${custom.正序颜色}">正序`, `${custom.逆序颜色}">逆序`];

        if (conf == '1') {
          updateItem(shsort + '_button', {
            title: obj.title.replace(titles[1], titles[0]),
          })
          putMyVar(shsort + '_sort', '0');
        } else {
          updateItem(shsort + '_button', {
            title: obj.title.replace(titles[0], titles[1]),
          })
          putMyVar(shsort + '_sort', '1');
        }
        //refreshPage()
        return 'toast://切换排序成功'
      }, vari, shsort, custom),
      col_type: custom.线路样式,
      extra: {
        id: shsort + '_button',
      }
    })
    this.createPList(stabs, vari, shsort, custom, parse);
  },
  setLists: function (lists, index, shsort, parse) {
    //log(lists)
    shsort = shsort ? shsort : 'shsort';
    storage0.putMyVar(shsort + '_list', lists);

    parse = parse ? parse : {};
    var 样式 = parse.样式 ? parse.样式 : '';
    var 图标 = parse.图标 ? parse.图标 : 'https://hikerfans.com/tubiao/movie/13.svg';
    var ajax = parse.动态数据 ? parse.动态数据 : false;

    if (ajax) {
      var list = lists;
      if (getMyVar(shsort + '_sort') == '1' && lists.length > 0) {
        list = lists.reverse();
      }
    } else {
      var list = lists[index];
      if (getMyVar(shsort + '_sort') == '1' && lists.length > 0) {
        list = lists[index].reverse();
      }
    }



    if (样式 == '') {
      样式 = list.length > 3 ? 'text_4' : 'text_2'
    }

    var extra = '';

    if (parse.extra != undefined) {
      extra = parse.extra
    }

    if (list.length > 0) {
      var plist = storage0.getMyVar('er_playlists', '{}');
      if (!plist.hasOwnProperty(shsort)) {
        plist[shsort] = {};
      }
      plist[shsort]['cls'] = shsort;
      plist[shsort]['col_type'] = 样式;
      plist[shsort]['pic'] = 图标;
      plist[shsort]['extra'] = extra;
      storage0.putMyVar('er_playlists', plist);
    }



    for (var j in list) {

      let lt = {
        title: list[j].title,
        url: list[j].url,
        col_type: 样式,
        pic: 图标,
        extra: {
          id: list[j].url.replace(/@lazyRule[\s\S]+/g, ''),
          cls: shsort,
        }
      };
      lt['extra'] = Object.assign({}, lt['extra'], extra);
      d.push(lt);
    }
  },
  evalRe: function (input, 函数) {
    let result = input;
    result = eval('input' + 函数);
    return result;
  },
  getTabs: function (parse) {
    var tabs = [];
    var arts = [];
    if (typeof parse.线路 == 'object') {
      arts = parse.线路;
    }
    if (arts.length == 0) {
      arts = pdfa(html, parse.线路);
    }
    var 线路名 = parse.线路名称;
    var 线路链接 = parse.线路链接 ? parse.线路链接 : '';
    var 替换函数 = '';
    if (parse.线路名称.includes('@@')) {
      线路名 = parse.线路名称.split('@@')[0];
      替换函数 = parse.线路名称.split('@@')[1];
    }
    for (var i in arts) {
      let linename = '';
      if (线路名 == '') {
        linename = arts[i];
      } else {
        linename = pdfh(arts[i], 线路名);
      }
      if (替换函数 != '') {
        linename = this.evalRe(linename, 替换函数);
      }
      tabs.push({ title: linename });
      if (线路链接 != '') {
        tabs[i]['url'] = pd(arts[i], 线路链接);
      }
    }
    //log(tabs);
    return tabs;
  },
  getLists: function (parse, shsort, h) {
    html = h || html;
    shsort = shsort ? shsort : 'shsort';
    var lists = [];
    var conts = [];
    if (typeof parse.选集 == 'object') {
      conts = parse.选集;
    }
    if (conts.length == 0) {
      try {
        conts = pdfa(html, parse.选集);
      } catch {
        toast('选集定位有误')
      }
    }

    let 替换函数 = '';
    var 选集名称 = parse.选集名称 ? parse.选集名称 : 'a&&Text';
    if (选集名称.includes('@@')) {
      选集名称 = parse.选集名称.split('@@')[0];
      替换函数 = parse.选集名称.split('@@')[1];
    }

    var 选集链接 = parse.选集链接 ? parse.选集链接 : 'a&&href';
    var 解析 = parse.解析 ? parse.解析 : '';
    var temp = [];

    for (var i in conts) {
      if (parse.选集列表 != undefined) {
        if (parse.选集列表 == 'object') {
          lists.push(conts[i].map(x => {
            return {
              title: x.title,
              url: x.url + 解析,
            }
          }))
        } else {
          lists.push(pdfa(conts[i], parse.选集列表).map(x => {
            return {
              title: pdfh(x, 选集名称),
              url: pd(x, 选集链接) + 解析,
            }
          }))
        }
      } else {
        var url = pd(conts[i], 选集链接);
        if (!选集链接.includes('href')) {
          url = pdfh(conts[i], 选集链接);
        }
        let title = pdfh(conts[i], 选集名称);
        title = 替换函数 != '' ? this.evalRe(title, 替换函数) : title;
        temp.push({
          title: title,
          url: url + 解析,
        })
      }
    }
    let 倒序 = parse.倒序 ? parse.倒序 : false;
    var results = lists.length ? lists : [temp];
    if (倒序) {
      results = results.map(x => x.reverse());
    }
    var plist = storage0.getMyVar('er_playlists', '{}');
    if (results.some(x => x.length > 0)) {
      if (!plist.hasOwnProperty(shsort)) {
        plist[shsort] = {};
      }
      plist[shsort]['length'] = results.length;
    };
    storage0.putMyVar('er_playlists', plist);
    return results;
  },
  getPlayers: function (parse, mode, func) {
    //log(mode)
    var play = 'er_playlist_';
    this.preLoad(func);
    this.close(parse.map(x => x.类型), parse.length);
    mode = mode ? mode : 1;

    let pe = [1];
    try {
      pe = parse.map(x => pdfa(html, x.线路).length).filter(y => y > 0);
    } catch { }


    parse.forEach((item, i) => {
      var tabs = [{ title: '主线' }];
      if (item.线路 != undefined) {
        tabs = this.getTabs(item);
      }
      putMyVar(play + i + '_tabs', tabs.map(x => x.title));

      if (item.动态数据) {
        if (mode == 1) {
          this.setTabs(tabs, item.类型, play + i, item);
        } else {
          this.setTabs2(tabs, item.类型, play + i, item, pe);
        }
        putMyVar('er_html', html);
        putMyVar('er_url', MY_URL);
        var info = { parse: item, mark: play + i, url: MY_URL };
        if (tabs.some(x => x.url)) {
          info['urls'] = tabs.map(x => x.url);
        }

        var lists = func(getMyVar('er_' + item.类型, '0'), info);
      } else {
        var lists = this.getLists(item, play + i);

        if (lists.some(x => x.length > 0)) {
          if (mode == 1) {
            this.setTabs(tabs, item.类型, play + i, item);
          } else {
            this.setTabs2(tabs, item.类型, play + i, item, pe);
          }
        }
      }
      if (getMyVar('er_' + item.类型 + 'fold', '1') == '1') {
        this.setLists(lists, getMyVar('er_' + item.类型, '0'), play + i, item);
      }
      d.push({
        col_type: 'blank_block'
      })
    })
  }
}


let Methods = {
  //多选模块
  MultiSelectMode: function (name, id, shsort) {
    return $('#noLoading#').lazyRule((datatitle, dataurl, shsort) => {
      function unique(arr) {
        const res = new Map()
        return arr.filter((a) => !res.has(a.url) && res.set(a.url, 1))
      }
      let duoselect = storage0.getMyVar(shsort + "_duoselect") ? storage0.getMyVar(shsort + "_duoselect") : [];
      if (duoselect.findIndex(x => x.url === dataurl) == -1) {
        duoselect.push({
          title: datatitle,
          url: dataurl
        })
        duoselect = unique(duoselect)
        updateItem(dataurl, {
          title: `““””<span style="color:#FF5733">` + datatitle,
        });
      } else {
        function removeByValue(arr, val) {
          for (var i = 0; i < arr.length; i++) {
            if (arr[i]['url'] == val) {
              arr.splice(i, 1);
              break;
            }
          }
        }
        removeByValue(duoselect, dataurl)
        updateItem(dataurl, {
          title: datatitle
        });
      }
      storage0.putMyVar(shsort + "_duoselect", duoselect);
      return "hiker://empty"
    }, name, id, shsort)
  },
}


$.exports = {
  Erji: Erji,
